mapper.update:

UPDATE product_detials
SET
      context_kh 		= COALESCE(#{context_kh}, '')
    , context_en  		= COALESCE(#{context_en}, '')
    , context_ch  		= COALESCE(#{context_ch}, '')
    , modify_date 	    = to_char(NOW(), 'YYYYMMDD')
    , modify_by		    = #{user_id}
    , status		    = #{status}
WHERE id 			    = #{id}


		update product_detials
        set
             status  		= #{status}
            , modify_by   	= #{user_id}
            , modify_date	= to_char(NOW(), 'YYYYMMDD')
        where id   			= #{id}
